mapper.select:
		select * from (
			select rownum limitNum, A.* from portfolio A where maker_id=#{makerId}
			and rownum <= (#{page}*10) order by A.id desc
		) where limitNum > ((#{page}-1)*10)

select * from (
	select rownum limitNum, A.* from portfolio A where type=#{category}
	and rownum <= (#{page}*10) order by A.id desc
) where limitNum > ((#{page}-1)*10)
