mapper.update:
UPDATE portfolio 
SET title=#{title}, type=#{category}, content=#{content}, maker_id=#{maker.id}, image=#{image}
WHERE id=#{id}

mapper.select:

FROM (
		FROM portfolio
		WHERE maker_id = #{makerId}
		ORDER BY id DESC)
		WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM portfolio
		WHERE type = #{category}
		ORDER BY id DESC)
		WHERE rnum BETWEEN #{begin} AND #{end}
