mapper.select:

FROM (
		FROM product
		WHERE type = #{category}
		ORDER BY hit DESC)
WHERE rnum BETWEEN 1 AND #{page}


FROM (
		FROM product
		WHERE type = #{category}
		ORDER BY id DESC)
WHERE rnum BETWEEN 1 AND #{page}


FROM (
		FROM product
		WHERE type = #{category}
		ORDER BY id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM product
		WHERE type = #{category} AND title = #{title}
		ORDER BY id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM product INNER JOIN member ON product.maker_id = member.id
		WHERE product.type = #{category} AND member.name = #{makerName}
		ORDER BY product.id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM product
		WHERE type = #{category} AND member_id = #{makerId}
		ORDER BY id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM product
		WHERE member_id = #{makerId}
		ORDER BY id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}


FROM (
		FROM product
		WHERE title = #{title} AND member_id = #{makerId}
		ORDER BY id DESC)
WHERE rnum BETWEEN #{begin} AND #{end}
