mapper.update:
UPDATE request
SET title = #{title}, maker_id=#{maker.id}, consumer_id=#{consumer.id}, type = #{category}, content = #{content}, hope_price=#{hopePrice}, price=#{price}, bound=#{bound} 
WHERE id = #{id}

mapper.select:

FROM request
, (select /*+INDEX_FES(table column) */ ceil(count(*)/${@ordermade.constants.Constants@REQUEST_ROW_SIZE}) page from request)
WHERE id = #{id}
