mapper.update:
UPDATE request
SET
title = #{title}, maker_id=#{maker.id}, consumer_id=#{consumer.id},
type = #{category}, content = #{content}, hope_price=#{hopePrice},
price=#{price}, bound=#{bound}
WHERE id = #{id}

mapper.select:

FROM(
	FROM request
	WHERE bound = #{bound}
	ORDER BY id DESC )
	WHERE rnum BETWEEN #{begin} AND #{end}
