mapper.update:
UPDATE request
SET title = #{title}, maker_id=#{maker.id}, consumer_id=#{consumer.id}, type = #{category}, content = #{content}, hope_price=#{hopePrice}, price=#{hopePrice}, bound=#{bound} 
WHERE id = #{id}

mapper.select:
select * from (
	select rownum limitNum, A.* from request A where bound = #{bound}
	and rownum <= (#{page}*${@ordermade.constants.Constants@PORTFOLIO_ROW_SIZE}) order by A.id desc
) where limitNum > ((#{page}-1)*${@ordermade.constants.Constants@PORTFOLIO_ROW_SIZE})


FROM (
	 FROM request 
	 WHERE consumer_id = #{consumerId} 
	 ORDER BY id DESC)       
WHERE rnum BETWEEN #{begin} AND #{end}
