INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '우리집밖에 없는 식탁!', 'FUNITURE', '집에 알맞은 크기, 소재, 높이와 문양까지 모두 고객님이 원하시는 대로 만들어 드렸습니다. 세상에 하나밖에 없는 우리집만의 식탁을 만들어드립니다. 사소한것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'funiture/0.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '밤에 공부할 때, 책 읽을 땐? 스탠드~', 'FUNITURE', '나에게 알맞은 크기, 소재, 높이와 문양까지 모두 고객님이 원하시는 대로 만들어 드렸습니다. 세상에 하나밖에 없는 나만의 스탠드를 만들어드립니다. 사소한것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'funiture/1.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '핸드폰을 더 특별하게! 핸드폰고리 ', 'ACCESSORY', '원하는 디자인과 색상, 큐빅 위치, 그리고 각인까지 모두 고객님이 원하시는 대로 만들어 드렸습니다. 세상에 하나밖에 없는 나만의 핸드폰고리를 만들어드립니다. 사소한것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'accessory/0.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '세상을 뚜렸하게! 나만의 안경', 'ACCESSORY', '원하는 디자인에 맞추어 만들어 드렸습니다. 크기, 소재, 모양까지 모두 고객님이 원하시는 대로 만들어 드렸습니다. 남들과 차별화된 나만의 안경을 만들어드립니다. 사소한것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'accessory/10.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '컴퓨터도 특별하게! 나만의 모니터', 'DIGITAL', '고객님이 원하는 inch, 모니터 높이, 디자인에 맞추어 만들어 드렸습니다. 사소한 것 하나까지 모두 고객님이 원하시는 대로 만들어 드렸습니다. 작은 것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'digital/0.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '특별한 스피커', 'DIGITAL', '고객님이 원하는 색상, 각인, 디자인에 맞추어 만들어 드렸습니다. 사소한 것 하나 놓치지 않고 디자인, 음질까지 확실한 스피커를 만들었습니다. 작은 것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'digital/1.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '디자인, 착용감까지 모두 잡은 신발', 'CLOTHING', '고객님이 원하는 색상, 높이, 각인, 디자인에 맞추어 만들었습니다. 작은 디테일까지 살리고, 발의 편안함까지 모두 잡은 신발를 만들었습니다. 작은 것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'clothing/0.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '패피 모두 모여라! 이제는 맞춤 옷이 대세!', 'CLOTHING', '공장에서 찍어낸 똑같은 옷이 아닌 고객님이 원하는 색상, 사이즈, 디자인에 맞추어 만들었습니다. 작은 디테일까지 살리고, 멋스러움은 더한 품질 좋은 옷입니다. 작은 것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'clothing/3.jpg', SYSDATE);								
INSERT INTO PORTFOLIO (id, title, type, content, maker_id, image, regdate) VALUES (portfolio_seq.nextval, '따르릉 따르릉은? 자전거!', 'SPORT', '고객님이 원하는 색상, 사이즈, 디자인 그리고 무게까지 맞추어 만들었습니다. 작은 디테일까지 살리고, 부품하나까지 좋은 것으로만 사용했습니다. 작은 것 하나라도 꼼꼼히 만들겠습니다.', 'm1', 'sport/3.jpg', SYSDATE);
